/* External CSS */
body {
    background-color: #f0f0f0;
    font-family: Arial, sans-serif;
}

h1 {
    color: blue;
    text-align: center;
}

/* Element Selector */
p { color: black; }

/* Class Selector */
.highlight { background: yellow; }

/* ID Selector */
#header { font-size: 24px; }

/* Multiple */
h1, h2, h3 { font-family: Arial; }

/* Colors */
h1 {
    color: #3498db;           /* Hex */
    background: rgb(255,0,0); /* RGB */
}

/* Fonts */
body {
    font-family: "Segoe UI", Arial, sans-serif;
    font-size: 16px;
    font-weight: bold;
    line-height: 1.6;
}

.card {
    width: 300px;
    padding: 20px;    /* Gudaha */
    border: 2px solid black;
    margin: 10px;     /* Dibadda */

    /* Border-box = padding gudaha width */
    box-sizing: border-box;
}

.container {
    display: block;        /* Row dhan */
}

.inline-items {
    display: inline-block; /* Iska raaca */
}

.flex-container {
    display: flex;         /* Flexbox */
}

.container {
    display: flex;
    justify-content: center;  /* Dhexda horizontally */
    align-items: center;      /* Dhexda vertically */
    gap: 20px;                /* Space items */
}

.item {
    flex: 1;  /* Qaybso space si siman */
}

.grid-container {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;  /* 3 columns */
    grid-template-rows: auto;
    gap: 20px;
}

/* Item spanning 2 columns */
.wide-item {
    grid-column: span 2;
}

/* Mobile first (default) */
.container {
    width: 100%;
    padding: 10px;
}

/* Tablet (768px+) */
@media (min-width: 768px) {
    .container {
        width: 750px;
        margin: 0 auto;
    }
}

/* Desktop (1024px+) */
@media (min-width: 1024px) {
    .container {
        width: 960px;
    }
}

.responsive-box {
    width: 80%;           /* 80% of parent */
    max-width: 1200px;    /* Maximum width */
    padding: 2rem;        /* Relative to root */
    font-size: 1.2em;     /* Relative to parent */
}
